/////////
// App //
/////////

@import "./variables.less";

// Components
@import "./components/logos.less";

html, body {
  background: @white;
}

.intro {
  background: @red;
  position: relative;
  height: 400px;

  .logo {
    position: absolute;
    top: 50%;
    left: 50%;
    margin-top: -(@logo-watermark-base-size / 2);
    margin-left: -(@logo-watermark-base-size / 2);
  }
}

