@import url(http://fonts.googleapis.com/css?family=Roboto:400,100,300,500,700,900,100italic,300italic,400italic,500italic,700italic,900italic|Roboto+Slab:400,100,300,700);
.container {
  position: relative;
  width: 100%;
  height: 480px;
}
section {
  display: flex;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  transition: opacity 1s ease-out, transform 0.8s ease-in, z-index 1s linear;
  opacity: 1;
  z-index: 2;
  transform: scale(1);
}
section .left-door,
section .right-door {
  position: relative;
  width: 50%;
  height: 100%;
  transition: transform 0.8s ease-in, box-shadow 0.5s linear;
  overflow: hidden;
}
section .inner {
  position: absolute;
  top: 0;
  left: 0;
  width: 200%;
  height: 100%;
}
section .right-door > .inner {
  left: -100%;
}
section.open {
  transform: scale(2);
  opacity: 0;
  pointer-events: none;
}
section.open .left-door,
section.open .right-door {
  box-shadow: 0 0 25px rgba(0, 0, 0, 0.9);
}
section.open .left-door {
  transform: translateX(-800px);
}
section.open .right-door {
  transform: translateX(800px);
}
section:not(.open) + section {
  z-index: 1;
  transform: scale(0.9);
  opacity: 0;
}
.section-white {
  color: #444444;
}
.section-white .left-door,
.section-white .right-door {
  background: #fafafa;
}
.section-red {
  color: #fafafa;
}
.section-red .left-door,
.section-red .right-door {
  background: #ed4253;
}
.logo-lettermark {
  background: url('/img/gb-lettermark.svg');
  width: 200px;
  height: 200px;
  background-size: 100%;
}
html,
body {
  font-family: Roboto;
  background: #fafafa;
  margin: 0;
  padding: 0;
  width: 100%;
  height: 100%;
}
body {
  display: flex;
  align-items: center;
}
.intro .logo {
  position: absolute;
  top: 50%;
  left: 50%;
  margin-top: -100px;
  margin-left: -100px;
}
